targets.expr:
topk(
  10,
  (
    sum by (groupname, ip, server) (
      label_replace(
        rate(namedprocess_namegroup_cpu_seconds_total[$__rate_interval]),
        "ip", "$1", "instance", "(.*):.*"
      )
    )
    /
    on(ip)
    group_left(server)
    count by (ip, server) (
      label_replace(
        node_cpu_seconds_total,
        "ip", "$1", "instance", "(.*):.*"
      )
    )
  ) * 100
)

