targets.expr:
topk(
  10,
  (
    sum by (groupname, ip) (
      label_replace(
        namedprocess_namegroup_memory_bytes,
        "ip", "$1", "instance", "(.*):.*"
      )
    )
    /
    on(ip)
    group_left
    sum by (ip) (
      label_replace(
        node_memory_MemTotal_bytes,
        "ip", "$1", "instance", "(.*):.*"
      )
    )
  ) * 100
)

topk(
  10,
  (
    sum by (groupname, ip) (
      label_replace(
        namedprocess_namegroup_memory_bytes{memtype="resident"},
        "ip", "$1", "instance", "(.*):.*"
      )
    )
    /
    on(ip)
    group_left(server)
    max by (ip, server) (
      label_replace(
        node_memory_MemTotal_bytes,
        "ip", "$1", "instance", "(.*):.*"
      )
    )
  ) * 100
)

sum by (instance, mode) (
  rate(
    node_cpu_seconds_total{
      instance=~"$instance",
      mode!="idle",
      mode!="iowait"
    }[$__rate_interval]
  )
) * 100

sum by (instance, mode) (
  rate(
    node_cpu_seconds_total{
      instance=~"$instance",
      mode!="idle",
      mode!="iowait"
    }[$__rate_interval]
  )
) * 100

topk(
  10,
  (
    sum by (server, container_label_com_docker_compose_service) (
      rate(container_cpu_usage_seconds_total{
        container_label_com_docker_compose_service!=""
      }[5m])
    )
    *
    on(server)
    group_left(ip)
    label_replace(
      node_uname_info,
      "ip", "$1", "instance", "(.*):.*"
    )
  )
)

topk(
  10,
  sum by (container_label_com_docker_compose_service, instance, server) (
    rate(container_cpu_usage_seconds_total{
      container_label_com_docker_compose_service!=""
    }[5m])
  ) * 100
)

topk(
  10,
  sum by (container_label_com_docker_compose_service, server, instance) (
    container_memory_working_set_bytes{
      container_label_com_docker_compose_service!=""
    }
  )
)
